class App extends React.Component {
  constructor (props) {
    super (props);

    this.state = {
      age: 0
    };
  }

  render() {
    return(
      <div class="all">
        <div class="setAge">
          <p>You are {age} years old</p>
          <button onClick={() => this.state.age + 1}>+</button>
          <button onClick={() => this.state.age - 1}>-</button>
          <br></br>
          <button onClick={() => this.state.age = 0}>Reset</button>
        </div>
      </div>
    )
  }
}

export default App;
