import React, { useState } from "react";
import "./RegisterPage.css";
import fireBackground from "./fire-background.jpg";

function RegisterPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    if (password !== confirmPassword) {
      setErrorMessage("Passwords do not match");
      setSuccessMessage("");
      return;
    }
    // send a request to register the user
   

    setErrorMessage("");
    setSuccessMessage("Registration Successful!");
  }

  return (
    <div className="register-page" style={{ backgroundImage: `url(${fireBackground})` }}>
      <div className="register-form-container">
        <h1>Register</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Username:
            <input
              className="register-input"
              type="text"
              value={username}
              onChange={(event) => setUsername(event.target.value)}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              className="register-input"
              type="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
            />
          </label>
          <br />
          <label>
            Confirm Password:
            <input
              className="register-input"
              type="password"
              value={confirmPassword}
              onChange={(event) => setConfirmPassword(event.target.value)}
            />
          </label>
          <br />
          <button className="register-button" type="submit">
            Register
          </button>
        </form>
        {errorMessage && <p className="register-error">{errorMessage}</p>}
        {successMessage && <p className="register-success">{successMessage}</p>}
      </div>
    </div>
  );
}

export {RegisterPage};
