import { useParams, Link } from 'react-router-dom';
import { useState } from 'react';

function PostsEdit() {
    const title = useParams();
    const [post, setPost] = useState(null);

    return (
      <div>
        <h2>{post.title}</h2>
        <Link to={`/posts/${title}/edit`}>Edit</Link>
      </div>
    )
}
  
export {PostsEdit};
