import React, { useState } from 'react';

function App() {
  // Створюємо нову змінну стану, яку назвемо "count"
  const [count, setCount] = useState(1);

  return (
    <div>
      <p>Ви натиснули {count} разів</p>
      <button onClick={() => setCount(count + 1)}>Click me</button>
    </div>
  );
}

export default App;
