import { useEffect, useState } from 'react';
import axios from 'axios';
import {useNavigate} from "react-router-dom";

import './loginpage.css';

function Loginpage({isLogin, setIsLogin}) { 
    const navigate = useNavigate();

    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [invalidAuth, setInvalidAuth] = useState(false);

    useEffect(() => {
        if (loginError || passwordError) {
            setIsLogin(false);
        }
    }, [loginError, passwordError]);

    const loginHandler = (e) => {
        const re = /^[a-zA-Z0-9_.]+$/
        if (!re.test(e.target.value)) {
            setLoginError('Incorrect login');
        } else if (e.target.value.length < 4 || e.target.value.length > 12) {
            setLoginError('Login must be more than 4 and less than 12 characters');
        } else {
            setLoginError('');
        }
        
        setInvalidAuth(false);
        setLogin(e.target.value);
    }

    const passwordHandler = (e) => {
        if (!e.target.value) {
            setPasswordError('Password cannot be empty');
        } else if (e.target.value.length < 4 || e.target.value.length > 10) {
            setPasswordError('Password must be more than 4 and less than 10 characters');
        } else {
            setPasswordError('');
        }
        
        setInvalidAuth(false);
        setPassword(e.target.value);
    }
    
    const authHandler = async () => {
        try {
            const response = await axios.get('/api/find/' + login);
            const base = response.data[0];
            
            if (!base || password !== base.password) {
                setInvalidAuth(true);
            } else {
                setIsLogin(true);
                setInvalidAuth(false);
                navigate('/');
            }
        } catch (error) {
            console.error(error);
        }
    };
    
    
    return (
        <form className="loginForm" onSubmit={handleLogin}>
            <h1 className="adminPanel">Admin Panel</h1>

            {invalidAuth && <div className="inputsError invalidAuth">Invalid login/password</div>}

            <div>
                {(loginDirty && loginError) && <div className='inputsError'>{loginError}</div>}
                <input
                    onChange={loginHandler}
                    onBlur={blurHandle}
                    name="login"
                    value={login}
                    className="loginFormInput"
                    type="text"
                    placeholder="Login"
                    autoComplete="username"
                    required                
                />
            </div>

            {(passwordDirty && passwordError) && <div className='inputsError'>{passwordError}</div>}
            <div>
                <input
                    onChange={passwordHandler}
                    onBlur={blurHandle}
                    name="password"
                    value={password}
                    className="loginFormInput"
                    type="password"
                    placeholder="Password"
                    autoComplete="current-password"
                    required                
                    />
            </div>

            <div>
                <button className='btnLogin' disabled={!formValid} type="submit" onClick={authHandler}>Login</button>
            </div>
        </form>
    )
}

export {Loginpage}
