import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: 'Сurrent time',
      date: new Date(),
      counter: 0
    }

    setInterval(() => {
      this.setState((state, props) => ({
        date: new Date(),
        counter: state.counter + 1
      }));
    }, 1000);
  }


  render() {
    return (
      <div>
        <p>Sdas</p>
      </div>
    );
  }
}

export default App;
