function Loginpage({isLogin}) {
    // const handleLogin = (e) => {
    //     e.preventDefault();
    //     setIsLogin(true);
    // }

    const handleLogin = () => setIsLogin({ id: '1', login: 'ira', password: '123' })
    const handleLogout = () => {setIsLogin(null)}

    return (
        <form className="loginForm" onSubmit={handleLogin}>
            <h1>Admin Panel</h1>
            <div>
                <input
                    className="loginFormInput"
                    type="text"
                    placeholder="Login"
                    required                
                />
            </div>

            <div>
                <input
                    className="loginFormInput"
                    type="password"
                    placeholder="Password"
                    required                
                />
            </div>

            <div>
            {isLogin ? (
                <button onClick={handleLogout}>Sign Out</button>
            ) : (
                <button onClick={handleLogin}>Sign in</button>
            )}
            </div>
        </form>
    )
}

export {Loginpage}
