//     <App />
//   </React.StrictMode>
// );
const root = ReactDOM.createRoot(document.getElementById('root'));

class Clock extends React.Ccomponent() {
    constructor(props) {
        super(props)
        this.state = {date: new Date()};
    }

    render () {
        return (
            <div>
                <hr></hr>
                <h2>This is Clock!</h2>
                <h3>Time is {this.state.date.toLocaleTimeString()}</h3>
            </div>
        );
    }
}


root.render(<Clock />);
